                <div key="following">팔로잉<br />{user.Followings.length}</div>,
                <div key="follower">팔로워<br />{user.Followers.length}</div>
            ]}>
            <Card.Meta
                avatar={<Avatar>{user.nickname[0]}</Avatar>}
                title={user.nickname}
            />
        </Card>
        <Button onClick={onLogout}>로그아웃</Button>
        </>
    );
}

export default UserProfile;
